SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD,UI_NAME,UI_MOBILE,UI_EMAIL,UI_BIRTH
FROM USER_INFO


		AND UI_NAME LIKE CONCAT('%',#{uiName},'%')


		AND UI_NUM = #{uiNum}


		AND UI_ID = #{uiId}

SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD,UI_MOBILE,UI_EMAIL,UI_BIRTH
FROM USER_INFO
WHERE UI_NUM = #{uiNum}

SELECT UI_NUM, UI_NAME, UI_ID, UI_PWD,UI_MOBILE,UI_EMAIL,UI_BIRTH 
FROM USER_INFO
WHERE UI_ID = #{uiId}
AND UI_PWD = #{uiPwd}
